var http=require("http");
var fs=require("fs")
var server=http.createServer();
server.on("request",function (req,res) {
    if(req.url!="/favicon.ico"){
        var url="."+req.url;
        fs.readFile(url,function (err,data) {
            if(err){
                res.write("page is not find")
            }else {
                res.write(data);
            }
            res.end()
        })
    }

})
server.listen(8888)